<?php function getMenu($menu,$vertical=true){	$style='';	echo "<ul type='none'>";	if (!$vertical)		$style="style='display:inline'";	foreach ($menu as $k=>$v){			echo "<li$style><a href=\"$v\">".$k."</a></li>  " ;	}	echo '</ul>';}function getTable($cols,$rows,$colors){	echo "<table border='1' align='center'>";	for ($i=1;$i<=$rows;$i++){				echo '<tr>';				for ($b=1;$b<=$rows;$b++){			$d=$b*$i;			if ($i==1 or $b==1) {				echo "<th bgcolor=\"$colors\">".$d.'</th>'; 			}else{				echo '<td>'.$d.'</td>';				}		}				echo '</tr>';			}	echo '</table>';}function clearData ($string){ 	$clearString=trim(strip_tags($string));	return $clearString;}?>